import React from 'react'
import styled, {css} from 'styled-components'
import {gray, black, white} from 'open-color'

const Wrapper = styled.div`
    & + & {
        margin-top: 1.2rem;
    }
`

const Input = styled.input`
    display: none;
    & + label > span{
        display: inline-block;
        width: 2rem;
        height: 2rem;
        padding: 0.6rem;
        box-sizing: border-box;
        margin-right: 0.8rem;
        border-radius: 1rem;
        background-color: ${gray[3]};
        & > span{
            display: none;
        }
    }
    &:checked + label span{
        span{
            display: block;
            width: 0.8rem;
            height: 0.8rem;
            background-color: ${white};
            border-radius: 0.4rem;
        }
        ${({theme}) => {
            const background = theme.primary
            return css`
                background-color: ${background};
            `
        }}
    }
    &:disabled{
        & + label{
            color: ${gray[3]};
            span{
                background-color: ${gray[1]};
            }
        }
        &:checked + label > span{
            ${({theme}) => {
                const background = theme.primary
                return css`
                    background-color: ${lighten(0.2, background)};
                `
            }}
        }
    } 

`
const Label = styled.label`
    display: flex;
    align-items: center;
    font-size: 1.5rem;
    color: ${black};
    cursor: pointer;
`

function Radio ({children, disabled, checked, name, value, onChange}) {
    return(
        <Wrapper>
            <Input
                type="radio"
                id={value}
                disabled={disabled}
                defaultChecked={checked}
                name={name}
                value={value}
                onChange={onChange}
            />
            <Label htmlFor={value}>
                <span><span></span></span>
                {children}
            </Label>
        </Wrapper>
    )
}

export default Radio
