import React from 'react'
import styled, {css} from 'styled-components'
import {gray, black, white} from 'open-color'
import {Icon16} from '../../icon/Icon'

const Wrapper = styled.div`
    & + & {
        margin-top: 1.2rem;
    }
`

const Input = styled.input`
    display: none;
    & + label span{
        display: inline-block;
        width: 2rem;
        height: 2rem;

        margin-right: 0.8rem;

        border-radius: 0.6rem;
        background-color: ${gray[3]};
        background-repeat: no-repeat;
        background-position: center;
    }
    &:checked + label span{
        svg{
            display: inline-block;
        }
        ${({theme}) => {
            const background = theme.primary
            return css`
                background-color: ${background};
            `
        }}
    }
    &:disabled{
        & + label{
            color: ${gray[3]};
            span{
                background-color: ${gray[1]};
            }
        }
        &:checked + label span{
            background-color: ${gray[5]};
        }
    } 

`
const Label = styled.label`
    display: flex;
    align-items: center;
    font-size: 1.5rem;
    color: ${black};
    cursor: pointer;
`

function Checkbox ({children, disabled, checked, name, value, onChange}) {
    return(
        <Wrapper>
            <Input
                type="checkbox"
                id={value}
                disabled={disabled}
                checked={checked}
                name={name}
                value={value}
                onChange={onChange}
            />
            <Label htmlFor={value}>
                <span>
                    <Icon16
                        width="16"
                        height="16"
                        fill={white} 
                        icon="IcoCheck"
                    />
                </span>
                {children}
            </Label>
        </Wrapper>
    )
}

export default Checkbox
