import React from 'react'
import styled from 'styled-components'
import {gray, black} from 'open-color'

const StyeldTextFieldsWrapper = styled.form`

`
const Label = styled.label`
    display: inline-block;
    font-size: 1.4rem;
    color: ${gray[7]};
    margin-bottom: 0.8rem;
`

const StyeldInputWrapper = styled.div`
    display: flex;
    border-radius: 1.2rem;
    border: 0.1rem solid ${gray[1]};
    overflow: hidden;
    :focus-within{
        border: 0.1rem solid red;
    }
`

const Input = styled.input`
    font-size: 1.6rem;
    color: ${black};
    border: 0;
    padding: 1.6rem;
    outline: none;
    flex: 1;
    ::placeholder{
        color: ${gray[4]};
    }
    :focus{

    }
`
const ErrorMessage = styled.p`

`
const HelpMessage = styled.p`

`

function TextFields ({ label, placeholder, defaultValue, name, disabled, error, errorMessage, helpMessage }) {
    return(
        <StyeldTextFieldsWrapper>
            <Label>{label}</Label>
            <StyeldInputWrapper>
                <Input
                    type="text"
                    placeholder={placeholder}
                    defaultValue={defaultValue}
                    name={name}
                    disabled={disabled}
                />
            </StyeldInputWrapper>
            {error && <ErrorMessage>{errorMessage}</ErrorMessage>}
            <HelpMessage>{helpMessage}</HelpMessage>
        </StyeldTextFieldsWrapper>
    )
}

export default TextFields
