import React from 'react'
import styled, {css} from 'styled-components'
import {gray, red, black} from 'open-color'
import { Icon16 } from '../icon/Icon'


const InputWrapper = styled.div`
    display: flex;
    align-items: center;
    border-radius: 1.2rem;
    border: 0.1rem solid ${gray[1]};
    overflow: hidden;
    cursor: pointer;
    ${props => props.disabled && css`
        background-color: ${gray[0]};
    `}
    ${props => props.error && css`
        border: 1px solid ${red[5]};
    `}
    :focus-within{
        border: 0.1rem solid ${props => props.theme.primary};
    }
    svg{
        margin-right: 1.6rem;
    }
`

const StyeldSelect = styled.select`
    font-family: 'Spoqa Han Sans Neo';
    font-size: 1.6rem;
    color: ${black};
    border: 0;
    padding: 1.6rem;
    outline: none;
    flex: 1;
    background: none;
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    ::-ms-expand{ display: none; }
    cursor: pointer;
`
function Select ({ children, name, disabled, error, onChange, onFocus, onBlur }) {
    return(
        <InputWrapper disabled={disabled} error={error}>
            <StyeldSelect
                type="text"
                name={name}
                disabled={disabled}
                onChange={onChange}
                onFocus={onFocus}
                onBlur={onBlur}
            >
                {children}
            </StyeldSelect>
            <Icon16
                width="16"
                height="16"
                color={gray[8]}
                icon="IcoChevronDown16"
            />
        </InputWrapper>
    )
}

export default Select
