import React from 'react'
import styled, {css} from 'styled-components'
import {gray, black} from 'open-color'

const Input = styled.input`
    display: none;
    & + label span{
        display: inline-block;
        width: 20px;
        height: 20px;

        margin-right: 8px;

        border-radius: 6px;
        background-color: ${gray[2]};

    }
    &:checked + label span{
        ${({theme}) => {
            const background = theme.primary
            return css`
                background-color: ${background};
            `
        }}
    }

`
const Label = styled.label`
    display: flex;
    align-items: center;
    font-size: 1.5rem;
    color: ${black};
    cursor: pointer;
`

function Checkbox ({children}) {
    return(
        <>
            <Input
                type="checkbox"
                name="dd"
                id="dd"
            />
            <Label for="dd">
                <span></span>
                {children}
            </Label>
        </>
    )
}

export default Checkbox
