import React from 'react'
import styled, {css} from 'styled-components'


const StyledIcon = styled.svg`
    path{
        width: 100%;
    }
`
function Icon ({ width, height, color, name, children, ...rest }) {
    return(
        <StyledIcon
            xmlns="http://www.w3.org/2000/svg"
            width={width}
            height={height}
            fill={color}
            {...rest}
        >
            {children}
        </StyledIcon>
    )
}

export default Icon
