import React from 'react'
import styled, { css } from 'styled-components'
import {gray, red, black} from 'open-color'

const StyeldForm = styled.form`
    font-family: 'Spoqa Han Sans Neo';
    & + &{
        margin-top: 1.6rem;
    }
`

const StyeldInput = styled.div`
    display: flex;
    border-radius: 1.2rem;
    border: 0.1rem solid ${gray[1]};
    overflow: hidden;
    ${props => props.disabled && css`
        background-color: ${gray[0]};
    `}
    ${props => props.error && css`
        border: 1px solid ${red[5]};
    `}
    :focus-within{
        border: 0.1rem solid ${props => props.theme.primary};
    }

`
const Label = styled.label`
    display: inline-block;
    font-size: 1.4rem;
    color: ${gray[7]};
    margin-bottom: 0.8rem;
`

const ErrorMessage = styled.p`
    display: inline-block;
    font-size: 1.4rem;
    color: ${red[5]};
    margin: 0.8rem 0 0;
`
const HelpMessage = styled.p`
    font-size: 1.4rem;
    color: ${black};
    margin: 0.8rem 0 0;
`

function Form ({ label, error, errorMessage, helpMessage, children}) {
    return(
        <StyeldForm>
            <Label>{label}</Label>
            <StyeldInput disabled={disabled} error={error}>
                {children}
            </StyeldInput>
            {error && <ErrorMessage>{errorMessage}</ErrorMessage>}
            {!error && <HelpMessage>{helpMessage}</HelpMessage>}
        </StyeldForm>
    )
}

export default Form
