import React from 'react'
import styled from 'styled-components'
import {gray, black} from 'open-color'

const StyledInput = styled.input`
    font-family: 'Spoqa Han Sans Neo';
    font-size: 1.6rem;
    color: ${black};
    border: 0;
    padding: 1.6rem;
    outline: none;
    flex: 1;
    background: none;
    ::placeholder{
        color: ${gray[5]};
    }
`
function TextInput ({ placeholder, defaultValue, name, disabled, onChange, onFocus, onBlur }) {
    return(
        <StyledInput
            type="text"
            placeholder={placeholder}
            defaultValue={defaultValue}
            name={name}
            disabled={disabled}
            onChange={onChange}
            onFocus={onFocus}
            onBlur={onBlur}
        />
    )
}

export default TextInput
