import React from 'react'
import styled, {css} from 'styled-components'
import {black} from 'open-color'

const Wrapper = styled.div`
    display: flex;
    align-items: center;
`

const Input = styled.input`
    & + span{
        width: 20px;
        height: 20px;
        border-radius: 6px;
        ${({theme}) => {
            const background = theme.primary
            return css`
                background-color: ${background};
            `
        }}
    }

`
const Label = styled.label`
    font-size: 1.5rem;
    color: ${black};
`

function Checkbox ({children}) {
    return(
        <Wrapper>
            <Input
                type="checkbox"
                name="dd"
                id="dd"
            />
            <span></span>
            <Label  for="dd">{children}</Label>
        </Wrapper>
    )
}

export default Checkbox
