import React from 'react'
import styled from 'styled-components'
import {gray} from 'open-color'

const AnchorElem = styled.a`
    font-size: 1.5rem;
    color: ${gray[5]};
    :hover{
        color: ${props => props.theme.primary};
    }
`

function Anchor ({href, target, children}) {
    return(
        <AnchorElem
            href={href}
            target={target}
        >
            {children}
        </AnchorElem>
    )
}

export default Anchor
