import React from 'react'
import styled, {css} from 'styled-components'
import {gray, black} from 'open-color'

const Wrapper = styled.div`
    & + & {
        margin-top: 12px;
    }
`

const Input = styled.input`
    display: none;
    & + label span{
        display: inline-block;
        width: 20px;
        height: 20px;
        margin-right: 8px;
        border-radius: 10px;
        background-color: ${gray[3]};
        span{
            display: none;
            width: 8px;
            height: 8px;
            background-color: ${white}
        }
    }
    &:checked + label span{
        span{
            display: block;
        }
        ${({theme}) => {
            const background = theme.primary
            return css`
                background-color: ${background};
            `
        }}
    }
    &:disabled{
        & + label{
            color: ${gray[3]};
            span{
                background-color: ${gray[1]};
            }
        }
        &:checked + label span{
            background-color: ${gray[4]};
        }
    } 

`
const Label = styled.label`
    display: flex;
    align-items: center;
    font-size: 1.5rem;
    color: ${black};
    cursor: pointer;
`

function Radio ({children, disabled, checked, name, id}) {
    return(
        <Wrapper>
            <Input
                type="radio"
                id={id}
                disabled={disabled}
                checked={checked}
                name={name}
            />
            <Label htmlFor={id}>
                <span><span></span></span>
                {children}
            </Label>
        </Wrapper>
    )
}

export default Radio
