    const stars = [1,2,3,4,5];
    return(
        <StyeldRating>
            {
                stars.map(star => (
                    <Star 
                        key={star}
                        active={star < activeCount + 1 && true}
                        onClick={onClick}
                    />
                ))
            }
        </StyeldRating>
    )
}

export default Rating
