import React from 'react'
import styled from 'styled-components'
import {gray, black} from 'open-color'
import { Icon24 } from '../icon/Icon'

const Wrapper = styled.div`
    display: inline-block;
    text-align: center;
    svg{
        fill: ${props => props.theme.primary};
    }
`
const Label = styled.p`
    text-align: center;
    margin: 0;
    line-height: 1.5rem;
`

function BottomBarAction ({ label, icon, active, ...rest}) {
    return(
        <Wrapper active={active}>
            <Icon24
                width="24"
                height="24"
                fill={active ? black : gray[4]} 
                icon={icon}
            />
            <Label>{label}</Label>
        </Wrapper>
    )
}

export default BottomBarAction
