import React from 'react'
import styled, {css} from 'styled-components'
import {gray, black} from 'open-color'


const StyeldInputWrapper = styled.div`
    display: flex;
    border-radius: 1.2rem;
    border: 0.1rem solid ${gray[1]};
    overflow: hidden;
    ${props => props.disabled && css`
        background-color: ${gray[0]};
    `}
    ${props => props.error && css`
        border: 1px solid ${red[5]};
    `}
    :focus-within{
        border: 0.1rem solid ${props => props.theme.primary};
    }

`

const StyledInput = styled.input`
    font-family: 'Spoqa Han Sans Neo';
    font-size: 1.6rem;
    color: ${black};
    border: 0;
    padding: 1.6rem;
    outline: none;
    flex: 1;
    background: none;
    ::placeholder{
        color: ${gray[5]};
    }
`
function TextInput ({ placeholder, defaultValue, name, disabled, onChange, onFocus, onBlur }) {
    return(
        <StyeldInputWrapper disabled={disabled} error={error}>
            <StyledInput
                type="text"
                placeholder={placeholder}
                defaultValue={defaultValue}
                name={name}
                disabled={disabled}
                onChange={onChange}
                onFocus={onFocus}
                onBlur={onBlur}
            />
        </StyeldInputWrapper>

    )
}

export default TextInput
