import React from 'react'
import styled, {css} from 'styled-components'
import { darken, lighten } from 'polished';
import {gray, white} from 'open-color'
import { SIZE, SHAPE } from './constants';
import { COLOR } from '../../../constants/color';

const sizeStyles = css`
    height : ${props => SIZE[props.size].height};
`

const shapeStyles = css`

    border-radius : ${props => SHAPE[props.shape].borderRadius};

    ${props => props.fullWidth && css`
        width: 100%;
        &:not(:first-child){
            margin-top: 1rem;
            margin-left: 0;
        }
    `}
`

const colorStyles = css`
    ${({ theme, color}) => {
        var background = COLOR[color].background
        if(color === 'primary'){
            background = theme[color]
        }
        const fontColor = COLOR[color].color
        return css`
            background: ${background};
            color: ${fontColor};
            &:active {
                background: ${darken(0.05, background)};
            }
            &:disabled{
                background: ${gray[1]};
                color: ${white};
            }
            ${props => props.outline && css`
                border: 0.1rem solid ${background};
                background: ${white};
                color: ${background};
                &:active {
                    background: ${lighten(0.2, background)};
                }
                &:disabled{
                    background: ${white};
                    border: 0.1rem solid ${gray[2]};
                    color: ${gray[2]};
                }
            `}
        `
    }}
`

const StyledButton = styled.button`
    /* 공통 스타일 */
    display: inline-flex;
    align-items: center;
    justify-content: center;
    box-sizing: border-box;

    font-family: 'Spoqa Han Sans Neo';
    font-size: 1.6rem;
    font-weight: bold;
    outline: none;
    border: none;
    color: white;

    cursor: pointer;
    padding: 0 1.6rem;

    &:not(:first-child){
        margin-left: 1rem;
    }
    svg:first-child{
        margin-right: 6px;
        margin-left: 0;
    }
    svg:last-child{
        margin-left: 6px
    }
    ${sizeStyles}
    ${colorStyles}
    ${shapeStyles} 
`

function Button({ children, color, size, shape, outline, disabled, fullWidth, onClick, icon, ...rest }) {
    return (
        <StyledButton 
            color={color} 
            size={size} 
            shape={shape}
            outline={outline}
            fullWidth={fullWidth}
            disabled={disabled}
            onClick={onClick}
            {...rest}
        >
            {children}
        </StyledButton>
    )
}

Button.defaultProps = {
    color : 'primary',
    size : 'medium',
    shape : 'rounded',
}

export default Button
