import React from 'react'

function Option ({ label, value, disabled, selected }) {
    return(
        <option value={value} disabled={disabled} selected={selected}>{label}</option>
    )
}

Option.defaultProps = {
    label: "Option",
    value: "value",
}

export default React.memo(Option)
