import React from 'react'
import styled, {css} from 'styled-components'


const StyledIcon = styled.img`
    width: 100%;
`
function Icon ({ width, height, color, name, children }) {
    return(
        <StyledIcon>
            <svg 
                xmlns="http://www.w3.org/2000/svg"
                width={width}
                height={height}
                fill={color}
                viewBox="0 0 16 16"
            >
                {children}
            </svg>

        </StyledIcon>
    )
}

export default Icon
