import React from 'react'
import styled, {css} from 'styled-components'
import { darken, lighten } from 'polished';

const sizes = {
    large : {
        padding : '1.25rem',
    },
    medium : {
        padding : '1rem',
    },
    small : {
        padding : '0.75rem',
    }
}

const shapes = {
    square : {
        borderRadius : '0',
    },
    rounded : {
        borderRadius : '16px',
    },
    pill : {
        borderRadius : '30px',
    }
}


const shapeStyles = css`
    ${({shape}) => css`
        padding : ${sizes[shape].padding};
    `}
`

const sizeStyles = css`
    ${({size}) => css`
        padding : ${sizes[size].padding};
    `}
`

const colorStyles = css`
    ${({ theme, color }) => {
        const selected = theme[color]
        return css`
            background: ${selected};
            &:hover {
                background: ${lighten(0.1, selected)};
            }
            &:active {
                background: ${darken(0.1, selected)};
            }
        `
    }}
`

const StyledButton = styled.div`
    /* 공통 스타일 */
    display: inline-flex;
    outline: none;
    border: none;
    border-radius: 12px;
    color: white;
    font-weight: bold;
    cursor: pointer;
    font-size: 1rem;

    /* 크기 */
    ${sizeStyles}

    /* 색상 */
    ${colorStyles}

    /* 기타 */
    & + & {
        margin-left: 1rem;
    }
`

function Button({ children, color, size, shape, ...rest }) {
    return <StyledButton color={color} size={size} shape={shape}{...rest}>{children}</StyledButton>;
}

Button.defaultProps = {
    color : 'primary',
    size : 'medium',
    shape : 'rounded',
}

export default Button
