import React from 'react'
import styled, {css} from 'styled-components'

const Wrapper = styled.div`
    display: flex;
`
const TabElem = styled.p`
    font-size: 1.5rem;
    line-height: 150%;
    margin: 0;
    padding: 1rem 0.8rem;
    ${props => props.active && css`
        font-weight: 500;
        :before{
            width: 100%;
            height: 0.2rem;
            background-color: ${props => props.theme.primary};
        }
    `}
`

function Tabs ({ fit }) {
    return(
        <Wrapper>
            <TabElem active>Tab1</TabElem>
            <TabElem>Tab2</TabElem>
            <TabElem>Tab3</TabElem>
        </Wrapper>
    )
}

export default Tabs
