import React from 'react'
import styled, {css} from 'styled-components'
import { darken, lighten } from 'polished';
import {gray, red, white} from 'open-color'
import { SIZE, SHAPE } from './constants';



const sizeStyles = css`
    height : ${props => SIZE[props.size].height};
`

const shapeStyles = css`
    border-radius : ${props => SHAPE[props.shape].borderRadius};
    ${props => props.fullWidth && css`
        width: 100%;
    `}
`

const colorStyles = css`
    ${({ theme, type }) => {
        var background = buttonType[type].background
        if(type === 'primary'){
            background = theme[type]
        }
        const fontColor = buttonType[type].color
        return css`
            background: ${background};
            color: ${fontColor};
            &:active {
                background: ${darken(0.05, background)};
            }
            &:disabled{
                background: ${gray[1]};
                color: ${white};
            }
            ${props => props.outline && css`
                border: 0.1rem solid ${background};
                background: ${white};
                color: ${background};
                &:active {
                    background: ${lighten(0.2, background)};
                }
                &:disabled{
                    background: ${white};
                    border: 0.1rem solid ${gray[2]};
                    color: ${gray[2]};
                }
            `}
        `
    }}
`

const StyledButton = styled.button`
    display: inline-flex;
    gap: 0.6rem;
    align-items: center;
    justify-content: center;
    box-sizing: border-box;
    padding: 0 1.6rem;

    font-family: 'Spoqa Han Sans Neo';
    font-size: 1.6rem;
    font-weight: bold;

    outline: none;
    border: none;
    color: white;
    cursor: pointer;

    ${sizeStyles}
    ${colorStyles}
    ${shapeStyles} 
`

function Button({ children, type, size, shape, outline, disabled, fullWidth, onClick, icon, ...rest }) {
    const buttonType = {
        primary : {
            background: theme.type,
            color: white
        },
        warnning : {
            background: red[5],
            color: white
        },
        mono : {
            background: gray[2],
            color: gray[9]
        },

    }
    return (
        <StyledButton 
            type={type} 
            size={size} 
            shape={shape}
            outline={outline}
            fullWidth={fullWidth}
            disabled={disabled}
            onClick={onClick}
            {...rest}
        >
            {children}
        </StyledButton>
    )
}

Button.defaultProps = {
    type : 'primary',
    size : 'medium',
    shape : 'rounded',
    outline: false,
    fullWidth: false,
    disabled: false,
}

export default Button
