import React from 'react'
import styled, {css} from 'styled-components'
import {gray} from 'open-color'

const Wrapper = styled.div`
    display: flex;


    ${props => props.fit ? css`
        p{
            flex: 1;
        }
    ` : css`
        overflow: scroll;
    `}
    border-bottom: 1px solid ${gray[2]};
    ::-webkit-scrollbar{
        display: none;
    }
`
const TabElem = styled.p`
    font-size: 1.5rem;
    line-height: 150%;
    text-align: center;
    margin: 0;
    padding: 1rem 0.8rem;
    ${props => props.active && css`
        font-weight: 500;
        border-bottom: 0.2rem solid ${props => props.theme.primary};
    `}

    & + &{
        margin-left: 1rem;
    }
`

function Tabs ({ fit }) {
    return(
        <Wrapper fit={fit}>
            <TabElem active>Tab1</TabElem>
            <TabElem>Tab2</TabElem>
            <TabElem>Tab3</TabElem>
            <TabElem>Tab3</TabElem>
            <TabElem>Tab3</TabElem>
            <TabElem>Tab3</TabElem>
            <TabElem>Tab3</TabElem>
            <TabElem>Tab3</TabElem>
            <TabElem>Tab3</TabElem>
            <TabElem>Tab3</TabElem>
            <TabElem>Tab3</TabElem>

        </Wrapper>
    )
}

export default Tabs
