import {gray, white, red} from 'open-color'
export const SIZE = {
    large : {
        height : '60px',
    },
    medium : {
        height : '52px',
    },
    small : {
        height : '46px',
    }
}
export const SHAPE = {
    square : {
        borderRadius : '0',
    },
    rounded : {
        borderRadius : '16px',
    },
    pill : {
        borderRadius : '30px',
    }
}
export const COLOR = {
    primary : {
        background: gray[9],
        color: white
    },
    warnning : {
        background: '#FF6B6B',
        color: white
    },
    mono : {
        background: '#E9ECEF',
        color: gray[9]
    }
}
