import React from 'react'
import styled, {css} from 'styled-components'
import {gray, black} from 'open-color'
//import Check from './assets/ico-check.svg'

const Wrapper = styled.div`
    & + & {
        margin-top: 12px;
    }
`

const Input = styled.input`
    display: none;
    & + label span{
        display: inline-block;
        width: 20px;
        height: 20px;

        margin-right: 8px;

        border-radius: 6px;
        background-color: ${gray[3]};
        background-image: url('./assets/ico-check.svg');
    }
    &:checked + label span{
        ${({theme}) => {
            const background = theme.primary
            return css`
                background-color: ${background};
            `
        }}
    }
    &:disabled{
        & + label{
            color: ${gray[3]};
            span{
                background-color: ${gray[1]};
            }
        }
        &:checked + label span{
            background-color: ${gray[4]};
        }
    } 

`
const Label = styled.label`
    display: flex;
    align-items: center;
    font-size: 1.5rem;
    color: ${black};
    cursor: pointer;
`

function Checkbox ({children, disabled, checked}) {
    return(
        <Wrapper>
            <Input
                type="checkbox"
                id={children}
                disabled={disabled}
                checked={checked}
            />
            <Label for={children}>
                <span></span>
                {children}
            </Label>
        </Wrapper>
    )
}

export default Checkbox
