import React from 'react'
import styled from 'styled-components'
import {gray, yellow} from 'open-color'
import {Icon24} from '../icon/Icon'
const StyledSvg = styled.span`
    cursor: pointer;
    & + & {
        margin-left: 0.4rem;
    }
`

function Star ({active, onClick, size}) {
    return(
        <StyledSvg onClick={onClick}>
            <Icon24
                width={size}
                height={size}
                fill={active ? yellow[4] : gray[2]} 
                icon="IcoStar"
            />
        </StyledSvg>
    )
}

export default Star
