import React from 'react'
import styled from 'styled-components'
import {gray, red, black} from 'open-color'

const StyeldForm = styled.form`
    font-family: 'Spoqa Han Sans Neo';
    & + &{
        margin-top: 1.6rem;
    }
`

const Label = styled.label`
    display: inline-block;
    font-size: 1.4rem;
    color: ${gray[7]};
    margin-bottom: 0.8rem;
`

const ErrorMessage = styled.p`
    display: inline-block;
    font-size: 1.4rem;
    color: ${red[5]};
    margin: 0.8rem 0 0;
`
const HelpMessage = styled.p`
    font-size: 1.4rem;
    color: ${black};
    margin: 0.8rem 0 0;
`

function Form ({ label, errorMessage, helpMessage, children}) {
    return(
        <StyeldForm>
            <Label>{label}</Label>
            {children}
            <ErrorMessage>{errorMessage}</ErrorMessage>
            <HelpMessage>{helpMessage}</HelpMessage>
        </StyeldForm>
    )
}

export default Form
