import React from 'react'
import styled, {css} from 'styled-components'
import {gray, black} from 'open-color'

const StyledOption = styled.option`
    font-size: 1.6rem;
    font-weight: 300;
    color: ${black};
    ${props => props.disabled && css`
        color: ${gray[6]};
    `}
`

function Option ({ label, value, disabled }) {
    return(
        <StyledOption value={value} disabled={disabled}>{label}</StyledOption>
    )
}

Option.defaultProps = {
    label: "Option",
    value: "value",
}

export default React.memo(Option)
