import React, { useState } from 'react'
import styled, { css, keyframes } from 'styled-components'
import { COLOR } from '../../../constants/color';
import {white} from 'open-color'

const toggleOn = keyframes`
    from {
        transform: translateX(0);
    }
    to {
        transform: translateX(1.9rem);
    }
`

const toggleOff = keyframes`
    from {
        transform: translateX(1.9rem);
    }
    to {
        transform: translateX(0);
    }
`

const SwicthWrapper = styled.div`
    display: flex;
    align-items: center;
    width: 4.8rem;
    height: 3rem;
    border-radius: 2.4rem;
    transition: 0.3s;

    ${props => props.active ? css`
        background-color: ${props.theme.primary};
        div{
            transform: translateX(1.9rem);
        }
    ` : css`
        background-color: ${COLOR.mono.background};
        div{
            transform: translateX(0rem);
        }
    `}
`
const Toggle = styled.div`
    width: 2.4rem;
    height: 2.4rem;
    margin: 0.3rem;
    border-radius: 1.2rem;
    background-color: ${white};
    transition: 0.3s;
`

function Switch ({ active }) {
    const [toggle, setToggle] = useState(active)
    return(
        <SwicthWrapper active={toggle} onClick={() => setToggle(!toggle)}>
            <Toggle/>
        </SwicthWrapper>
    )
}

export default Switch
