import React from 'react'
import styled, {css} from 'styled-components'
import { darken, lighten } from 'polished';

const sizes = {
    large : {
        height : '60px',
    },
    medium : {
        height : '52px',
    },
    small : {
        height : '46px',
    }
}

const shapes = {
    square : {
        borderRadius : '0',
    },
    rounded : {
        borderRadius : '16px',
    },
    pill : {
        borderRadius : '30px',
    }
}


const shapeStyles = css`
    ${({shape}) => css`
        border-radius : ${shapes[shape].borderRadius};
    `}
`

const sizeStyles = css`
    ${({size}) => css`
    height : ${sizes[size].height};
    `}
`

const colorStyles = css`
    ${({ theme, color }) => {
        const background = theme[color].background
        const fontColor = theme[color].color
        return css`
            background: ${background};
            color: ${fontColor};
            &:active {
                background: ${darken(0.05, background)};
            }
        `
    }}
`

const StyledButton = styled.div`
    /* 공통 스타일 */
    display: inline-flex;
    align-items: center;

    font-weight: bold;
    font-size: 16px;
    outline: none;
    border: none;
    color: white;

    cursor: pointer;
    font-size: 1rem;
    padding: 0 16px;

    /* 크기 */
    ${sizeStyles}

    /* 색상 */
    ${colorStyles}

    /* 모양 */
    ${shapeStyles}
    /* 기타 */
    & + & {
        margin-left: 1rem;
    }
`

function Button({ children, color, size, shape, outline, ...rest }) {
    return (
        <StyledButton 
            color={color} 
            size={size} 
            shape={shape}
            outline={outline}
            {...rest}
        >
            {children}
        </StyledButton>
    )
}

Button.defaultProps = {
    color : 'primary',
    size : 'medium',
    shape : 'rounded',
}

export default Button
